import React, {
  Component
} from 'react';
import logo from './logo.svg';
import './App.css';
import Title from './Title'
import Input from './Input'
import Button from './Button'

class App extends Component {
  constructor(){
    super()
    this.state = {
      persons: [
        {name: "黄晓彬", age: 25},
        {name: "黄小明", age: 21},
        {name: "黄绯红", age: 45},
        {name: "黄种", age: 35}
      ],
      isShow: false
    }
  }
  showContent = (e) => {
    this.setState({
      isShow: !this.state.isShow
    })
  }
  changeName = (e, index) => {
    let persions = this.state.persions
    let persion = persions[index]
    persion.name = e.target.value;
    this.setState({
      persions: persions
    })
  }
  render() {
    let persons = null
    if(this.state.isShow){
      persons = (
        <div>
        {
          this.state.persons.map((person, index) => {
            return <div>
                <Title name={person.name}></Title>
                <Input name={person.name} changeName={(event) => this.changeName(event, index)} ></Input>
              </div>
          })
        }
        </div>
      )
    }
    return (
      <div className="App">
      <Button showContent={this.showContent.bind(this)}></Button>
      <br/>
        {persons}
      </div>
    );
  }
}

export default App;
