import React, {
  Component
} from 'react';
import logo from './logo.svg';
import './App.css';
import Title from './Title'
import Input from './Input'
import Button from './Button'

class App extends Component {
  constructor(){
    super()
    this.state = {
      name: "黄晓彬",
      isShow: false
    }
  }
  showContent = (e) => {
    this.setState({
      isShow: !this.state.isShow
    })
  }
  changeName = (e) => {
    this.setState({
      name: e.target.value
    })
  }
  render() {
    return (
      <div className="App">
      {
        this.state.isShow ? <Button showContent={this.showContent.bind(this)}></Button> : null
      }
      <Title name={this.state.name}></Title>
      <Input name={this.state.name} changeName={this.changeName.bind(this)} ></Input>
      </div>
    );
  }
}

export default App;
