import React, {Component} from 'react'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Title from './components/Title'
import Input from './components/Input'
import Button from './components/Button'
import Delete from './components/Delete'

class content extends Component {
  constructor(){
    super()
    this.state = {
    }
  }
  showContent = (e) => {
    this.setState({
      isShow: !this.state.isShow
    })
  }
  changeName = (e, index) => {
    console.log(index);
    let persons = this.state.persons
    let persion = {
      ...persons[index]
    }
    persion.name = e.target.value;
    persons[index] = persion
    this.setState({
      persons: persons
    })
  }
  deletePerson = (e, index) => {
    let persons = this.state.persons
    persons.splice(index, 1)
    this.setState({
      persons: persons
    })
  }
  render() {
    let butStyle = {
      color: "green",
      backgroundColor: "red"
    }
    let persons = null
    if(this.state.isShow){
      persons = (
        <div>
        {
          this.state.persons.map((person, index) => {
            return <div  key={index}>
                <Title name={person.name}></Title>
                <Input ></Input>
                <Delete deletePerson={(event) => this.deletePerson(event, index)} ></Delete>
              </div>
          })
        }
        </div>
      )
    }
    return (
		<div className="App">
			<Button style={this.state.isShow ? butStyle:null} showContent={this.showContent.bind(this)}></Button>
			<br/>
			{persons}
		</div>	
    );
  }
}
