import React, {Component} from 'react'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {changeName} from '../actions/personsAction'

class Input extends Component{
	componentDidMount(){
		// this.props.changePersons();
	}
	render(){
		return (
			<div>
				<input type="text" onChange={this.props.changeName} value={this.props.persons} />
			</div>
		);	
	}
}
const mapStateToProps = state => ({
	persons: state.persons.items
})

export default connect(mapStateToProps, {changeName})(Input)
